
public class NumericalExpressions2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("15+4: "+(15+4));
		
		System.out.println("15-4: "+(15-4));
		
		System.out.println("15*4: "+(15*4));
		
		System.out.println("15/4 gives the quotient: "+(15/4));
		
		System.out.println("15+4 gives the remainder: "+(15%4));
		
		System.out.println("23+4 gives the remainder: "+(23%4));
		
		System.out.println("divisor*quotient+remainder = original number "+(4*(23/4)+(23%4)));
		
		System.out.println("outputs 1+1 verbatim: " + "1 + 1");
		System.out.println("outputs result of additiong of 1+1 "+ (1+1));
		
		System.out.println("Gives result with precision (5.2/2): "+	(5.2/2));
		System.out.println("Gives result with precision (5/2.5): " +(5/2.5));

	}

}
