
public class StringVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String firstName  = "";
		String lastName = "";
		int i = 0;
		System.out.printf("Person %d %s, %s", i,lastName,firstName);
		System.out.println();
		
		firstName = "Bob";
		lastName = "Billy";
		i = i+1;
		System.out.printf("Person %d %s, %s", i,lastName,firstName);
		System.out.println();
		
		firstName = "Mo";
		lastName = "Joe";
		i = i+1;
		System.out.printf("Person %d %s, %s", i,lastName,firstName);
		System.out.println();
		

	}

}
